INSERT 
  INTO SYS_GPGM 
      (SYS_ID, TRAN_A, REGI_ID, REGI_DATE, CHNG_ID, CHNG_DATE, GROUP_ID, PROG_ID)
SELECT SYS_ID, TRAN_A, REGI_ID, REGI_DATE, CHNG_ID, CHNG_DATE, 'SYSTEM', PROG_ID 
  FROM SYS_PROG A
 WHERE NOT EXISTS (SELECT 1 FROM SYS_GPGM WHERE GROUP_ID = 'SYSTEM' AND PROG_ID = A.PROG_ID);

INSERT 
  INTO SYS_GPGM 
      (SYS_ID, TRAN_A, REGI_ID, REGI_DATE, CHNG_ID, CHNG_DATE, GROUP_ID, PROG_ID)
SELECT SYS_ID, TRAN_A, REGI_ID, REGI_DATE, CHNG_ID, CHNG_DATE, 'USER01', PROG_ID 
  FROM SYS_PROG A
 WHERE NOT EXISTS (SELECT 1 FROM SYS_GPGM WHERE GROUP_ID = 'USER01' AND PROG_ID = A.PROG_ID)
   AND A.PROG_ID NOT LIKE '/common/%';

